var fs = require("fs");
module.exports = function (req,res,next) {
	global.viewPath = (global.dirname+"/view") +"/";
	global.format ="." + "html";
	function trim(str){
		str.replace(/ /gi,"");
		return str;
	}
	res.render = function ( viewName , json) {
		fs.readFile(global.viewPath+"/"+viewName+global.format,"utf-8",function(err,data){
			res.send(data);
		});
	}
	next();	
}